    &- \frac{\sin\vartheta}{r\cos\varphi} \frac{\partial}{\partial\vartheta} \left( \cos\vartheta\cos\varphi\frac{\partial}{\partial r} -\frac{\sin\vartheta}{r\cos\varphi} \frac{\partial}{\partial\vartheta} -\frac{\cos\vartheta\sin\varphi}{r} \frac{\partial}{\partial\varphi} \right) \\
    &- \frac{\cos\vartheta\sin\varphi}{r} \frac{\partial}{\partial\varphi} \left( \cos\vartheta\cos\varphi\frac{\partial}{\partial r} -\frac{\sin\vartheta}{r\cos\varphi} \frac{\partial}{\partial\vartheta} -\frac{\cos\vartheta\sin\varphi}{r} \frac{\partial}{\partial\varphi} \right) \\
    %
    \frac{\partial^2}{\partial y^2} &= \sin\vartheta\cos\varphi\frac{\partial}{\partial r}\frac{\partial}{\partial y} + \frac{\cos\vartheta}{r\cos\varphi} \frac{\partial}{\partial\vartheta}\frac{\partial}{\partial y} -\frac{\sin\vartheta\sin\varphi}{r} \frac{\partial}{\partial\varphi}\frac{\partial}{\partial y} \\
    &= \sin\vartheta\cos\varphi\frac{\partial}{\partial r} \left( \sin\vartheta\cos\varphi\frac{\partial}{\partial r} + \frac{\cos\vartheta}{r\cos\varphi} \frac{\partial}{\partial\vartheta} -\frac{\sin\vartheta\sin\varphi}{r} \frac{\partial}{\partial\varphi} \right) \\
